$(function () {
    var $help = $("#login-nav li")
    $help.mouseover(function () {
        $("#help-list", this).stop().show();
    });
    $help.mouseout(function () {
        $("#help-list", this).stop().hide();
    });
    
    $(".search-btn").click(function () {
        $("#search-bar").show();
    });
    $("#close").click(function () {
        $("#search-bar").hide();
    });
    
    
    $(".menu-btn").click(function () {
        $("#Mobile-back").fadeIn();
    });
    $(".Mobile-back").click(function () {
        $(".Mobile-back").fadeOut();
    });
    
    
    $(".menu-btn").click(function () {
        $(".slide-togg").toggleClass("opne");
    });
    $(".Mobile-back").click(function () {
        $(".slide-togg").removeClass("opne");
    });
    
    $("#fot-tog1").click(function () {
        $("#tog1").slideToggle();
        $("#fot-tog1").toggleClass("fa-minus");
        $("#fot-tog1").toggleClass("fa-plus");
    });
    
    $("#fot-tog2").click(function () {
        $("#tog2").slideToggle();
        $("#fot-tog2").toggleClass("fa-minus");
        $("#fot-tog2").toggleClass("fa-plus");
    });

});
