$(function () {
    var $help = $("#login-nav li")
    $help.mouseover(function () {
        $("#help-list", this).stop().show();
    })
    $help.mouseout(function () {
        $("#help-list", this).stop().hide();
    });

    $(".menu-btn").click(function () {
        $(".Mobile-slide").addClass("opne");
    });
    $(".Mobile-back").click(function () {
        $(".Mobile-slide").removeClass("opne");
    });
});
