$(function () {
    //swiper 768以下で起動
    var swiper;
    $(window).on('load resize', function () {
        var w = $(window).width();
        if (w <= 1024) {
            if (swiper) {
                return;
            } else {
                swiper = new Swiper('.swiper1', {
                    loop: false,
                    freeMode: true,
                    slidesPerView: "auto",
                    spaceBetween: 0,
                    pagination: '.swiper-pagination',
                    nextButton: '.swiper-button-next',
                    prevButton: '.swiper-button-prev',
                    scrollbar: {
                        el: '.swiper-scrollbar',
                        hide: false,
                        draggable: true
                    },
                });
            }
        } else {
            if (swiper) {
                swiper.destroy();
                swiper = undefined;
            }
        }
    });
});


