$(function () {
    var mySwiper = new Swiper('.swiper1', {
        loop: true,
        slidesPerView: 1,
        pagination: '.swiper-pagination',
        nextButton: '.swiper-button-next',
        prevButton: '.swiper-button-prev',
        scrollbar: {
            el: '.swiper-scrollbar',
            hide: false,
            draggable: true
        },
    });



    var mySwiper = new Swiper('.swiper2', {
        loop: false,
        freeMode: true,
        slidesPerView: "3.05",
        spaceBetween: 0,
        pagination: '.swiper-pagination',
        nextButton: '.swiper-button-next',
        prevButton: '.swiper-button-prev',
        scrollbar: {
            el: '.swiper-scrollbar',
            hide: false,
            draggable: true
        },
        breakpoints: {
            // 300px以上の場合
            375: {
                slidesPerView: "1.09",
            },
            // 768px以上の場合
            768: {
                slidesPerView: "2.1",
            },
            // 1200px以上の場合
            1024: {
                slidesPerView: "2.8",
            }
        }
    });
});
